mapper.select:
SELECT TAKE 
FROM PROJECT
WHERE PROJECTKEY = #{prjkey}

mapper.insert:
INSERT INTO COST VALUES(
	(SELECT NVL(MAX(COSTKEY)+1,1) FROM COST),
	#{prjkey},
	'미승인',
	#{prjkey}
)

mapper.update:
UPDATE cost  SET
COSTASSIGN = '승인'
WHERE PROJECTKEY = #{prjkey}

BEGIN
MERGE INTO COSTDETAIL CO USING DUAL ON( CO.COINDEX = #{item.coindex} AND CO."NO" = #{prjkey} ) WHEN MATCHED THEN UPDATE SET COSTCONTENT = #{item.costcontent}, COSTNOTE = #{item.costnote}, COSTEX = #{item.costex}, COSTSORTKEY = #{item.cskey} WHEN NOT MATCHED THEN INSERT (NO, COSTCONTENT, COSTNOTE, COSTEX, COSTSORTKEY, COINDEX) VALUES (#{prjkey},#{item.costcontent},#{item.costnote},#{item.costex},#{item.cskey},#{item.coindex});
END;

mapper.delete:
DELETE FROM COSTDETAIL
WHERE NO = #{prjkey}
AND COINDEX = #{coindex}
