mapper.select:
select *
from (
select rownum cnt, b.*
from quality b
where 1=1

	and title like '%'||#{title}||'%'

) 
where cnt between #{start} and #{end} 
ORDER BY qualitykey

update quality
set
title=#{title},
contents = #{contents}
where qualitykey = #{qualitykey}

update.foreach:
UPDATE EVALITEM 
SET CONTENT = #{item.evalcontent}
WHERE EVALITEMKEY = #{item.evalkey}

mapper.update:
UPDATE PROJECT 
SET QUALITY = '합격'
WHERE PROJECTKEY = #{prjkey}
