mapper.select:
select *
from (
select rownum cnt,m.*
from member m
where 1=1

	and name like '%'||#{name}||'%'


	and email like '%'||#{email}||'%'

)
where cnt between #{start} and #{end}
order by memberkey

select *
from member
where id=#{id} and pass=#{pass}

mapper.update:
